SELECT per.studentnumber,
		id.firstName,
		id.lastName,
		sch.name,
		cal.calendarid,
		cal.name
FROM
	dbo.Person per WITH (NOLOCK) 
	LEFT OUTER JOIN dbo.Enrollment enr WITH (NOLOCK) on per.personID=enr.personID   and enr.startdate <= GETDATE() and (enr.enddate >= getdate() or enr.enddate is null) and enr.servicetype = 'P'
	LEFT OUTER JOIN dbo.calendar cal WITH (NOLOCK) on enr.calendarid = cal.calendarid
	LEFT OUTER JOIN dbo.school sch WITH (NOLOCK) on sch.schoolid = cal.schoolid
    LEFT OUTER JOIN dbo.schoolyear sy with (NOLOCK) on sy.endyear = enr.endyear and sy.active = 1
	INNER JOIN [dbo].[Identity] id WITH (NOLOCK) on id.identityid = per.currentidentityid
